import React from "react";

function Poem({ title, content, author, id, poems, setPoem }) {
  const [isRead, setIsRead] = useState(false);

  function handleDeletePoem() {
    fetch(` http://localhost:8004/poems/${id}`, {
      method: "DELETE"
    })
    .then((r) => r.json())
    .then(() => {
      const currentPoems = poems.filter((poem) => {
        return poem.id !== id
      }) 
      setPoem(currentPoems)
    })
  }

  function handleIsRead() {
    setIsRead(!isRead);
  }
  return (
    <div>
      <h3>{ title }</h3>
      <p>{ content }</p>
      <p>
        <strong>- By { author }</strong>
      </p>
      <button onClick={handleIsRead}>Mark as read</button>
      <button onClick={handleDeletePoem}>Delete this poem</button>
    </div>
  );
}

export default Poem;
