    fetch(` http://localhost:8004/poems/${id}`, {
      method: "DELETE"
    })
    .then((r) => r.json())
    .then(() => {
      const currentPoems = peoms.filter((poem) => {
        return poem.id !== id
      }) 
      setPoem(currentPoems)
    })
  }
  return (
    <div>
      <h3>{ title }</h3>
      <p>{ content }</p>
      <p>
        <strong>- By { author }</strong>
      </p>
      <button>Mark as read</button>
      <button onClick={handleDeletePoem}>Delete this poem</button>
    </div>
  );
}

